import React , {useState , useEffect}  from 'react'
import MyFaq from './MyFaq';

const FaqQuestion = [
    {
        id: 1,
        question: 'What is Faq ? ',
        answer: 'A frequently asked questions list is often used in articles, websites, email lists, and online forums where common questions tend to recur, for example through posts or queries by new users related to common knowledge gaps'
    },
    {
        id: 2,
        question: 'What is React Js  ? ',
        answer: 'React is a free and open-source front-end JavaScript library for building user interfaces based on UI components. It is maintained by Meta and a community of individual developers and companies.'
    },
    {
        id: 3,
        question: 'What is Heroku ? ',
        answer: 'Heroku is a cloud platform as a service supporting several programming languages. One of the first cloud platforms, Heroku has been in development since June 2007, when it supported only the Ruby programming language, but now supports Java, Node.js, Scala, Clojure, Python, PHP, and Go.'
    },
  {
    id: 4,
    question: 'What is Vercel ? ',
    answer: 'We enable developers to build and publish wonderful things.'
  },
  {
    id: 5,
    question: 'What is Netlify ? ',
    answer: 'Netlify is a San Francisco-based cloud computing company that offers hosting and serverless backend services for web applications and static websites.'
  }
  ]
  
const Fac = () => {
    const [questions, setQuestions] = useState(FaqQuestion)
    const API = FaqQuestion;
  const fetchFaq = () => {
    fetch(API)
      .then((res) => res.json())
      .then((res) => {
        console.log(res)
        setQuestions(res)
      })
  }
  useEffect(() => {
    fetchFaq()
  }, [])
  return (
    <main>
    <div className='container'>
    <img src={require('../assets/téléchargement.png')} />
      <section className='info'>
        {questions.map((question) => (
          <MyFaq key={question.id} {...question} />
        ))}
      </section>
    </div>
  </main>
  )
}

export default Fac
